import React from "react";
import { Line } from "react-chartjs-2";

function LineGraph({ xData, yData }) {
  const data = {
    labels: xData,
    datasets: [
      {
        label: "Line Graph",
        data: yData,
        fill: false,
        borderColor: "rgba(75,192,192,1)",
        tension: 0.1,
      },
    ],
  };

  const options = {
    scales: {
      xAxes: [
        {
          scaleLabel: {
            display: true,
            labelString: "X Axis",
          },
        },
      ],
      yAxes: [
        {
          scaleLabel: {
            display: true,
            labelString: "Y Axis",
          },
        },
      ],
    },
    plugins: {
      tooltip: {
        mode: "nearest",
      },
      legend: {
        display: true,
        position: "bottom",
      },
    },
  };

  return (
    <div>
      <h2>Line Graph</h2>
      <Line data={data} options={options} />
    </div>
  );
}

export default LineGraph;
