import React from "react";
import styled from "styled-components";
import LineGraph from "./lineGraph";
import TableDisplay from "./table";

const Container = styled.div`
  position: relative;
  top: 0;
  left: 0;
  height: 100%;
  display: flex;
  justify-content: space-evenly;
  align-items: center;
  flex-wrap: wrap; /* Wrap the child elements */
  transition: opacity 0.2s ease-in-out;
  &.fade-enter-active {
    opacity: 1;
  }
  animation: fadein 2.5s;
  -webkit-animation: fadein 2.5s; /* Safari and Chrome */
  -moz-animation: fadein 2.5s; /* Firefox */
  -o-animation: fadein 2.5s; /* Opera */
  z-index: 9999;
`;

const Content = styled.div`
  max-width: 800px;
  position: relative;
  display: flex;
  flex-direction: column; /* Set the flex direction to column */
  align-items: center;
  padding: 24px;
  border-radius: 5px;
  box-shadow: 0px 0px 5px 2px rgba(0, 0, 0, 0.35);
  flex-shrink: 0; /* Prevent the component from shrinking */
`;

const CloseButton = styled.button`
  position: absolute;
  top: 3px;
  right: 3px;
  background-color: rgba(255, 0, 0, 0.6);
  border: none;
  border-radius: 5px;
  box-shadow: 0px 0px 4px 2px rgba(0, 0, 0, 0.35);
  cursor: pointer;
`;

const GraphContainer = styled.div`
  width: 400px; /* Set a maximum width for each graph */
`;

const Tag = styled.div`
  font-weight: bold;
`;

const Popup = ({ isOpen, togglePopup, children }) => {
  return (
    <>
      {isOpen && (
        <Container>
          <Content>
            <CloseButton onClick={togglePopup}>
              <Tag>X</Tag>
            </CloseButton>
            {children.map((child, index) => {
              if (child.type === "linegraph") {
                return (
                  <GraphContainer key={index}>
                    <LineGraph
                      xData={child.data.xData}
                      yData={child.data.yData}
                      title={child.data.title}
                    />
                  </GraphContainer>
                );
              } else if (child.type === "table") {
                return <TableDisplay key={index} data={child.data} />;
              } else {
                return null;
              }
            })}
          </Content>
        </Container>
      )}
    </>
  );
};

export default Popup;
