import React, { useState } from "react";
import styled from "styled-components";

const StyledPopup = styled.div`
  position: fixed;
  width: 100vw;
  height: 100vh;
  top: 0;
  left: 0;
  background-color: rgba(0, 0, 0, 0.5);
  z-index: 999;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const PopupContent = styled.div`
  background-color: white;
  padding: 20px;
  border-radius: 10px;
`;

const Button = styled.button`
  background-color: #1e90ff;
  color: white;
  border: none;
  border-radius: 5px;
  padding: 10px 20px;
  cursor: pointer;
`;

const PopupForm = styled.form`
  display: flex;
  flex-direction: column;
  gap: 10px;
`;

const Input = styled.input`
  padding: 5px;
  border-radius: 5px;
  border: 1px solid #ccc;
`;

const Label = styled.label`
  font-weight: bold;
`;

const PopupWindow = ({ formTitle, formFields, onSubmit }) => {
  const [isOpen, setIsOpen] = useState(false);

  const handleButtonClick = () => {
    setIsOpen(true);
  };

  const handleClose = () => {
    setIsOpen(false);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    onSubmit(event.target);
    handleClose();
  };

  return (
    <>
      <Button onClick={handleButtonClick}>Open Popup</Button>
      {isOpen && (
        <StyledPopup onClick={handleClose}>
          <PopupContent onClick={(event) => event.stopPropagation()}>
            <h2>{formTitle}</h2>
            <PopupForm onSubmit={handleSubmit}>
              {formFields.map((field) => (
                <React.Fragment key={field.name}>
                  <Label>{field.label}</Label>
                  <Input type={field.type} name={field.name} />
                </React.Fragment>
              ))}
              <Button type="submit">Submit</Button>
            </PopupForm>
          </PopupContent>
        </StyledPopup>
      )}
    </>
  );
};

export default PopupWindow;
